<template>
  <div class="notice-bar" :style="'background:'+ barColor + ';width:'+ width+ ';height:'+height">
    <transition name="slide">
      <div class="text" :key="text.id" :style="'height:'+height +';width:' + width" >
        <img v-if="hasIcon&&!simpleList" :src="text.img" style="height: 50%;border-radius: 50%;" alt="">
        <div class="text-notice" :style="'font-size:'+ fontSize" >
          <span v-if="text.val">{{text.val}}</span>
          <slot></slot>
          <span v-if="!$slots.default&&text.action&&!simpleList">
                {{ text.action }}
              </span>
          <span :style="'color:'+ contentColor">{{text.content}}</span>
        </div>
        <div :style="'font-size:'+ fontSize">{{text.timeStr}}</div>
      </div>
    </transition>
  </div>
</template>

<script>
  export default {
    name: 'infinite-notice-bar',
    data () {
      return {
        backgroud: 'background:url("data:image/jpeg;base64,")',
        number: 0,
        notices: [
          {
            name: 'bacchusgift@outlook.com',
            icon: 'http://thirdwx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTL1MSckkg8QpA8iaBiaG5OWXoqhj8EHQpYEDxKD7mgIJOlpeZ5ZAkBYldmKdlY8HoeA6nwfy6Rtzwug/132',
            action: '',
            content: 'star',
            timeStr: '1'
          },
          {
            name: '',
            icon: 'http://thirdwx.qlogo.cn/mmopen/vi_32/Q0j4TwGTfTL1MSckkg8QpA8iaBiaG5OWXoqhj8EHQpYEDxKD7mgIJOlpeZ5ZAkBYldmKdlY8HoeA6nwfy6Rtzwug/132',
            content: 'star',
            timeStr: '3'
          }
        ]
      }
    },
    props: {
      noticeList: {
        type: Array,
        default: () => []
      },
      timeStr: {
        type: Boolean,
        default: false
      },
      contentColor: {
        type: String,
        default: '#c95354'
      },
      hasIcon: {
        type: Boolean,
        default: true
      },
      barColor: {
        type: String,
        default: 'rgba(250, 251, 255, 0.5);'
      },
      action: {
        type: String,
        default: ''
      },
      simpleList: [],
      width: {
        type: String,
        default: '9rem'
      },
      height: {
        type: String,
        default: '1.2rem'
      },
      speed: {
        type: Number,
        default: 2000
      },
      fontSize :{
        type: String,
        default: '0.3rem'
      }
    },
    created () {
      this.startMove()
      if (this.noticeList) {
        this.notices = this.noticeList
      }
    },
    computed: {
      text () {
        if (this.simpleList) {
          return {
            id: this.number,
            val: this.simpleList[this.number]
          }
        }
        return {
          id: this.number,
          val: this.notices[this.number].name.length > 15 ? this.notices[this.number].name.slice(0, 15) + '...' : this.notices[this.number].name,
          img: this.notices[this.number].icon,
          action: this.notices[this.number].action ? this.notices[this.number].action : this.action,
          content: this.notices[this.number].content,
          timeStr: this.notices[this.number].timeStr ? this.notices[this.number].timeStr : this.timeStr ? ['', '3', '1', '2'][Math.floor(Math.random() * 4)] : ''
        }
      },
      simpleText () {
        return {
          id: this.number,
          val: this.simpleList[this.number],
          img: '',
          content: '',
          timeStr: ''
        }
      }
    },
    methods: {
      startMove () {
        // eslint-disable-next-line no-use-before-define
        clearTimeout(timer)
        let timer = setTimeout(() => {
          if (this.number === (this.notices.length - 1)) {
            this.number = 0
          } else {
            this.number += 1
          }
          this.startMove()
        }, this.speed)
      }
    }
  }
</script>
