  .slide-enter-active, .slide-leave-active {
    transition: all 0.5s linear;
  }
  .slide-enter{
    transform: translateY(1rem) scale(1);
    opacity: 1;
  }
  .slide-leave-to {
    transform: translateY(-1rem) scale(1);
    opacity: 0;
  }
  .notice-bar {
    /*background:linear-gradient(90deg,rgba(255,164,126,1) 0%,rgba(255,134,94,1) 64%,rgba(255,86,39,1) 100%);*/
    border-radius: 2rem;
    background-size: 1.2rem 9rem;
    margin-top: 0.2rem;
    height: 1.2rem;
    position: absolute;
    display: flex;
    justify-items: center;
    z-index: 1;
    overflow: hidden;
  }
  .text {
    margin-left: 0.3rem;
    position: absolute;
    display: flex;
    align-items: center;
    overflow: hidden;
    justify-content: start;
  }
  .text-notice {
    margin-left: 0.2rem;
    width: 70%;
    /*font-weight:bold;*/
    overflow: hidden;
  }
</style>
